// 传入文件的绝对路径
python compile.py -f /home/jerry/rustproj/cita/admintool/txtool/txtool/tests/test.sol

// 或者传入源码
python compile.py -s  "pragma solidity ^0.4.0;  contract SimpleStorage {      uint storedData;      event Init(address, uint);      event Set(address, uint);      function SimpleStorage() {          storedData = 100;          Init(msg.sender, 100);      }      event Stored(uint);      function set(uint x)  {          Stored(x);          storedData = x;          Set(msg.sender, x);      }      function get() constant returns (uint) {          return storedData;      }  }" 

